package MyWorks;

public class forLoopII {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
	for (int x=0 ; x<5 ; x++) {
		for (int y=0 ; y<x ; y++) {
		System.out.print(" ");
		
	}
		for (int k=5-x ; k>=1 ; k--) {
			
			System.out.print(k+ " ");
		}
		
		System.out.println();
	
	}	
		
}
}
