package Week3;

public class BasicLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	for (char i= 'A' ; i<='Z' ; i++) {
		
		
		System.out.print(i + " ");
		
		
		
	}
		
		System.out.println("");
	
	for (int a=65 ; a<=90 ; a++ ) {
		
		System.out.print((char)a+ " ");
	}
		
	   System.out.println("");
	
	for (int i='Z' ; i>='A' ; i--) {
		
		System.out.print((char)i + " ");
		
		
	}
	
		
	}

}
