package Week3;

public class ContineAndBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=0 ; i<10 ; i++) {
			
			if ( i == 3 || i ==7) {
				continue;
			}
			
			if (i == 8) {
				
				break;
			}
			
			System.out.println(i);
			
		}
		
		System.out.println("---------------------------------");
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
