package day04_arithmatic_operators;

public class aritmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(55+5);
		
		int chairs=140;
		System.out.println(chairs+2);
		chairs=chairs+2;
		
		
		int moreChairs=chairs+5;
		System.out.println(moreChairs);
		
		int outcome=12+6/3;
		System.out.println(outcome);
		
		int tables=130;
		int total=tables+chairs;
		
		System.out.println(total);
		
		
		double balance=1200.50;
		double transaction=56.44;
		System.out.println(balance-transaction);
		balance=balance-transaction;
		System.out.println(balance);
		
		
		
		int linesOfCode=50;
		System.out.println(linesOfCode);
		
		int total2=linesOfCode-2;
		System.out.println(total2);
		
		System.out.println("Lines Of code " + linesOfCode);
		System.out.println("Lines Of code " + total2);
		
		System.out.println(60/5);
		System.out.println(10/2);
		
		System.out.println(10%5);
		System.out.println(9%2);
		
		
		
		
		
		
		
		

	}

}
