public class task46_MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("Multiplication table for 7");
		System.out.println("--------------------------");
		
		for (int a=5, b=1 ; b<=10 ; b++) {
		
		System.out.println(a + " * " + b + " = " + (a*b) );
		}
		
		
		
	}
	


}
